mapper.select:
SELECT IF(ISNULL(c.username),'系统',c.nick_name) inviter_name, SUM(b.`success_money`) AS success_money, SUM(b.total_money) AS total_money, SUM(b.total_count) AS total_count, SUM(b.success_count) AS success_count, c.`username` FROM item_stat b LEFT JOIN (SELECT * FROM childrenlst WHERE LEVEL=2) AS a ON INSTR(CONCAT(',',a.childrenlst,','),CONCAT(',',b.inviter,','))>0 LEFT JOIN partner c ON a.uuid=c.`uuid` WHERE b.stat_time>=CONCAT(REPLACE(#{beginTime},"-",""),"00")
AND b.stat_time <= concat(replace(#{endTime},"-",""),"24")
AND c.username= #{username}
AND b.product_id in (
#{item}
GROUP BY a.childrenlst;
