function autoQuery(payTransactionId)
{
	$.ajax({
		type:"GET",
		url:"/pay/query/"+payTransactionId+".json",
		dataType:"json",
		success:function(e){
			switch(e.message.operateCode)
			{case operateResultSuccess:case 710010:
				alert(e.message.message);
				location.href="/order/index.shtml?page=1&rows=10" 			
			}
		},
		error:function(){
			return alert(e.message.operateCode),!1
		}
	})
}
function query(payTransactionId){
			$.ajax({
				type:"GET",
				url:"/pay/query/"+payTransactionId+".json",           
				async:false,
				success:function(data){
					if(data.message.operateCode==710010){		
						alert(data.message.message);
				        location.href="/content/notice/paySuccess.shtml";
						return false;
					}else if(data.message.operateCode==710013){
						 alert("暂未支付成功！");
					}	
							
				}
			})
	} 
	var times=5;
	function autoQuery(payTransactionId){
			$.ajax({
				type:"GET",
				url:"/pay/query/"+payTransactionId+".json",           
				async:false,
				success:function(data){
					times--;
					if(data.message.operateCode==710010){		
						alert(data.message.message);
				        location.href="/order/index.shtml?page=1&rows=10";
						return false;
					}else if(data.message.operateCode==710013&&times>0){
						clearInterval(timer);
						setTimeout("autoQuery('"+payTransactionId+"')",3000);
					}else if(times==0){
						$(".content .box_container_right").append('<p class="fo-13 martop10"><span class="red">注：若在半分钟之外支付，查询结果需要点击下方按钮显示</span></p>');
						$(".box_QRcode").append('<button onClick=query("'+payTransactionId+'")  style="margin: 30px auto;display: block;background-color: #ff6400;border: 0;color: #fff;padding: 2px 5px;outline:none">点击查询支付结果</button>');
					}					
				}
			})
	}	